INSERT INTO peces (id_pez, id_investigador, nombre_comun, nombre_cientifico, especie, dieta, descripcion, ejemplares, tamanio_maximo, peligro_extincion) VALUES (1, 1, 'Pez Payaso', 'Amphiprioninae', 'Perciforme', 'Omnívoro', 'Pequeño pez marino, que forma parte del paisaje de los arrecifes de coral del océano Indo-Pacífico. Se caracteriza por sus intensos colores naranja y blanco bordeados por una delgada franja negra. Vive conjuntamente con las anémonas.', 1500, 0.11, FALSE), (2, 1, 'Lubina', 'Dicentrarchus labrax', 'Perciforme', 'Carnívoro', 'Pez de silueta alargada y esbelta. En su cabeza encontramos una prominente mandíbula con labios carnosos y la boca repleta de dientes, situados sobre los maxilares, en los huesos de la bóveda del paladar.', 300000, 1.0, FALSE), (3, 2, 'Tiburón Peregrino', 'Cetorhinus maximus', 'Elasmobranquio', 'Carnívoro', 'Es el segundo pez más grande del mundo después del tiburón ballena. Aunque su silueta desde la superficie puede recordar a la de un tiburón cazador, en realidad es inofensivo ya que se alimenta de plancton filtrando el agua.', 25, 11.0, TRUE), (4, 1, 'Manta de espina', 'Mobula Mobular', 'Miliobatiforme', 'Carnívoro', 'Gran manta con una boca ancha y subterminal, márgenes anteriores del disco rectos a ligeramente convexos, espiráculos en forma de hendidura y ubicados en la superficie dorsal de la cabeza, bandas dentales anchas, una espina caudal dentada generalmente presente detrás de la aleta dorsal y aleta dorsal con punta blanca.', 435, 3.5, TRUE), (5, 2, 'Lenguado Común', 'Solea Solea', 'Pleuronectiforme', 'Carnívoro', 'Pez plano de cuerpo ovalado casi regular y muy comprimido, con cabeza pequeña redondeada. Escamas ctenoides y rectangulares, rudas al tacto.', 150000, 0.45, FALSE), (6, 1, 'Pez Globo', 'Tetraodontidae', 'Tetraodontiforme', 'Omnívoro', 'Considerados el tercer vertebrado más venenoso del mundo. Por lo general son habitantes de los trópicos y mares templados alrededor del mundo.', 5000, 0.61, FALSE);
INSERT INTO investigaciones (id_investigacion, id_investigador, titulo, resumen, lugar, fecha, hora) VALUES (1, 1, 'Investigación Arrecife de Coral', 'Encuentro con un arrecife de coral con diversas especies, encontrados nuevos especímenes de pez payaso; encuentro con una manta de espina a la vuelta al barco.', 'Oceano Índico', '2024-11-02', '09:30:00'), (2, 1, 'Exploración Océano Pacífico', 'Exploración cerca de la superficie por el oceano Pacífico, único encuentro con un tiburón peregrino.', 'Oceano Pacífico', '2024-11-03', '12:15:00'), (3, 2, 'Encuentros Costa de Cádiz', 'Exploración casual por la costa de Cádiz, varios encuentros con lubinas y lenguados, sorprendente encuentro con un espécimen de pez globo, le decidí capturar y traerlo de vuelta para llevarlo a un lugar más seguro.', 'Océano Atlántico', '2024-11-04', '10:00:00'), (4, 2, 'Encuentro Costa Índica', 'Investigación a fondo alrededor de una isla en el Océano Índico, se encontraron varios ejemplares de peces globo y una migración de mantas de espina.', 'Océano Índico', '2024-11-05', '11:00:00'), (5, 1, 'Investigación Pesca Local', 'Investigación llevada a cabo en un barco pesquero local para ver las diferentes especies con las que interactuaban los pescadores, se encontraron muchos ejemplares de lubinas, poco más que mencionar.', 'Oceano Atlántico', '2024-11-06', '15:00:00'), (6, 2, 'Emergencia Marítima', 'Unos pescadores hicieron una llamada de emergencia debido a que se pensaba que estaban siendo atacados por un tiburón, resultó ser un tiburón peregrino, los cuales son inofensivos para los humanos.', 'Océano Pacífico', '2024-11-07', '09:00:00');
